mapper.update:
UPDATE cafehi_order
SET order_status = #{order_status}, order_updatetime = #{order_updatetime}
WHERE order_code = #{order_code}

mapper.select:
  	SELECT om.order_menu_code, om.order_code, om.menu_code, om.total_order_price, om.total_order_count, o.member_code, o.order_status, o.order_writetime , o.order_updatetime, o.include_delivery
	FROM cafehi_order o LEFT OUTER JOIN cafehi_order_menu om
	ON o.order_code = om.order_code AND o.member_code = #{member_code}
