mapper.select:
SELECT COUNT(*) FROM cafehi_qna cq, cafehi_member_auth cma
WHERE cq.member_code = cma.member_code 
AND cma.member_auth = #{role_user}

mapper.update:
UPDATE cafehi_qna
SET qna_hit = qna_hit + 1
WHERE qna_num = #{qna_num}

mapper.select:
SELECT  * FROM(
SELECT q.qna_num, q.qna_title, q.classification, q.qna_content, q.qna_writetime, q.qna_hit, m.member_id, q.upload_path, q.store_file_name, q.upload_file_name FROM cafehi_qna q, cafehi_member m WHERE m.member_code = q.member_code
)
  WHERE qna_num=#{qna_num}

		SELECT * FROM (

		SELECT rownum rn, qna_num, qna_title, qna_content, qna_writetime, qna_hit, upload_path, filename, member_id, classification FROM(
		SELECT cq.qna_num, cq.qna_title, cq.qna_content, cq.qna_writetime, cq.qna_hit, cq.upload_path, cq.filename, cm.member_id, cq.classification 
		FROM cafehi_qna cq, cafehi_member cm, cafehi_member_auth cma 
		WHERE cm.member_code = cma.member_code AND cm.member_code = cq.member_code AND cma.member_auth = #{role_user} 
        ORDER BY qna_num DESC )

		)

	 			WHERE rn > (#{cri.pageNum} -1) * #{cri.amount} AND rn <= #{cri.pageNum} * #{cri.amount}
